package com.github.cnlinjie.infrastructure.util;

import org.apache.commons.codec.binary.Base64;

import java.util.regex.Matcher;
import java.util.regex.Pattern;

/**
 * @author linjie
 * @version 0.0.1
 * @date 16-7-30
 */
public final class Base64Utils {


    public static byte[] decode(String base64Str) {
        base64Str = base64Str.replaceFirst("^data:image/[^;]*;base64,?","");
        byte[] base64 = Base64.decodeBase64(base64Str);
        return base64;
    }

    public static Base64Byte decodeGetType(String base64Str) {
        Base64Byte base64 = new Base64Byte();
        byte[] decode = decode(base64Str);
        base64.bases = decode;
        Pattern r = Pattern.compile("^data:image/[^;]*");
        Matcher matcher = r.matcher(base64Str);
        if (matcher.find()) {
            String group = matcher.group(0);
            String type = group.replaceFirst("data:image/", "");
            base64.type = type;
        }
        return base64;
    }

    public static class Base64Byte {

        public String type;
        byte[] bases;

        public String getType () {
            return type;
        }

        public byte[] getBases () {
            return bases;
        }
    }

    public static void main (String[] args) {
        String baseStr = "data:image/jpg;base64,";
        Base64Byte base64Byte = decodeGetType(baseStr);
        System.out.println(base64Byte.getType());
    }


}
